import { useRef, useState } from "react";

const SycTimer = () => {

  const [sycTime, setSycTime] = useState(null);
  const [now, setNow] = useState(null);
  const intervalRef = useRef(null);

  // function getSycTime(timestamp) {
  function getSycTimer() {
    clearInterval(intervalRef.current);
    // setSycTime(timestamp);
    setSycTime(Date.now());
    setNow(Date.now());
    intervalRef.current = setInterval(() => {
      setNow(Date.now());
    }, 1000);
  }

  function handleStop() {
    clearInterval(intervalRef.current);
  }

  let secondsPassed = 0;
  if (sycTime != null && now != null) {
    secondsPassed = (now - sycTime) / 1000;
  }

  return (
    <>
      <div>
        {secondsPassed.toFixed(0)}
        <span className="ps-1">seconds ago</span>
      </div>
      <button onClick={getSycTimer} >Start</button>
      <button onClick={handleStop} >Stop</button>
    </>
  )
}

export default SycTimer
