import TxButton from '../../../../components/button/TxButton'
import Drawer from 'react-modern-drawer'
import 'react-modern-drawer/dist/index.css'
import { useState } from 'react'

const PledgeBtn = ({drawerContent, text}) => {
  const [isOpen, setIsOpen] = useState(false)
  const toggleDrawer = () => {
    setIsOpen((prevState) => !prevState)
  }

  return (
    <>
      <TxButton 
        className="bg-button-tx-color text-white tx-button-add-collateral"
        text={text}
        handleClick = {toggleDrawer}
      />
      <Drawer
        open={isOpen}
        onClose={toggleDrawer}
        direction='bottom'
        size='750px'
        className='details-page rounded-t-5xl'
        duration={200}
      >
        <div className="flex justify-end">
          <button onClick={toggleDrawer}>
            <svg aria-hidden="true" class="w-5 h-5" fill="currentColor" viewBox="0 0 20 20" xmlns="http://www.w3.org/2000/svg"><path fill-rule="evenodd" d="M4.293 4.293a1 1 0 011.414 0L10 8.586l4.293-4.293a1 1 0 111.414 1.414L11.414 10l4.293 4.293a1 1 0 01-1.414 1.414L10 11.414l-4.293 4.293a1 1 0 01-1.414-1.414L8.586 10 4.293 5.707a1 1 0 010-1.414z" clip-rule="evenodd"></path></svg>
          </button>
        </div>
        {drawerContent}
      </Drawer>
    </>
  )
}

export default PledgeBtn
