import { useState } from "react"
import { useChain } from "../../../../chain/ChainProvider"
import TxCard from "../../../../components/cards/TxCard"
import Listbox from "../../../../components/listbox/Listbox"

const AddCollateralTx = () => {
  const { activeChain, chains, changeChain } = useChain()
  const [selected, setSelected ] = useState(activeChain);
  return (  
    <TxCard>
      <div>
        <div className="text-lg font-Montserrat text-token-font">
          Select LP Token ID
        </div>
        <Listbox 
          selected={selected}
          items={chains}
          setSelected={setSelected}
        />
      </div>
    </TxCard>
  )
}

export default AddCollateralTx
