import clsx from "clsx";
import React, { useState } from "react";
import { createPortal } from "react-dom";
import { Transition } from "@headlessui/react";

const Drawer = ({ children, drawerContent, text, className }) => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleDrawer = () => {
    setIsOpen(!isOpen);
  };

  const handleClose = () => {
    setIsOpen(false);
  };

  const drawer = (
    <div className="fixed inset-0 z-50">
      <div className="absolute inset-x-0 bottom-0 w-80 bg-white h-2/3 overflow-auto p-4">
        <div className="flex justify-end">
          <button onClick={toggleDrawer}>
            <svg aria-hidden="true" className="w-5 h-5" fill="currentColor" viewBox="0 0 20 20" xmlns="<http://www.w3.org/2000/svg>"><path fillRule="evenodd" d="M4.293 4.293a1 1 0 011.414 0L10 8.586l4.293-4.293a1 1 0 111.414 1.414L11.414 10l4.293 4.293a1 1 0 01-1.414 1.414L10 11.414l-4.293 4.293a1 1 0 01-1.414-1.414L8.586 10 4.293 5.707a1 1 0 010-1.414z" clipRule="evenodd"></path></svg>
          </button>
        </div>
        {drawerContent}
      </div>
      <Transition
        show={isOpen}
        enter="transition-opacity duration-150"
        enterFrom="opacity-0"
        enterTo="opacity-100"
        leave="transition-opacity duration-150"
        leaveFrom="opacity-100"
        leaveTo="opacity-0"
      >
        <div
          className="fixed inset-0 bg-black bg-opacity-50"
          onClick={handleClose}
        />
      </Transition>
    </div>
  );

  return (
    <>
      <button
        type="button"
        className={clsx(
          "w-full h-12 items-center rounded-10px px-3 py-2 text-base font-semibold font-Poppins",
          className
        )}
        onClick={toggleDrawer}
      >
        {text}
      </button>
      {createPortal(drawer, document.body)}
    </>
  );
};

export default Drawer;
