import { useKnownTokens } from "../../web3/KnownTokensProvider"
import Banner from "./components/banner/Banner"

const Borrow = () => {

  const { loanTokens } = useKnownTokens()

  return (
    <>
      <Banner 
        title={<>Borrow cypto with<br />fixed-rates</>}
      />
      <ul>
        <div className="mx-auto max-w-7xl grid grid-cols-3 gap-7 pt-11 pb-52">
          {loanTokens.map((item, idx) => (
            <Card 
              key={idx}
              tokenIcon={item.icon}
              tokenSymbol={item.symbol}
            >
              <div className='pt-12 pb-10'>
                <DrawerButton 
                  className='text-button-tx-color bg-reclaim-btn tx-button-add-collateral'
                  text='Reclaim'
                />
              </div>
            </Card>
          ))}
        </div>
      </ul>
    </>

  )
}

export default Borrow
