import React, { useState } from "react";
import { createPortal } from "react-dom";

const Drawer = ({ children, drawerContent }) => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleDrawer = () => {
    setIsOpen(!isOpen);
  };

  const handleClose = () => {
    setIsOpen(false);
  };

  const drawer = (
    <div className="fixed inset-0 z-50 bg-black bg-opacity-50" onClick={handleClose}>
      <div className="w-80 bg-white h-screen overflow-auto p-4">
        <div className="flex justify-end">
          <button onClick={toggleDrawer}>
            <svg aria-hidden="true" class="w-5 h-5" fill="currentColor" viewBox="0 0 20 20" xmlns="http://www.w3.org/2000/svg"><path fill-rule="evenodd" d="M4.293 4.293a1 1 0 011.414 0L10 8.586l4.293-4.293a1 1 0 111.414 1.414L11.414 10l4.293 4.293a1 1 0 01-1.414 1.414L10 11.414l-4.293 4.293a1 1 0 01-1.414-1.414L8.586 10 4.293 5.707a1 1 0 010-1.414z" clip-rule="evenodd"></path></svg>
          </button>
        </div>
        {drawerContent}
      </div>
    </div>
  );

  return (
    <>
      <button onClick={toggleDrawer}>
        click to drawer
      </button>
      {isOpen && createPortal(drawer, document.body)}
    </>
  );
};

export default Drawer;
