import { createContext, useContext, useMemo } from "react";
import { toPercentage } from "../helpers/format-number";
import DaiWethSvg from "../components/tokens/svgs/DaiWethSvg";
import UsdcWethSvg from "../components/tokens/svgs/UsdcWethSvg";


const Context = createContext();

export function useKnownTokens() {
  return useContext(Context);
}

const KnowTokensProvider = ({ children }) => {

  const dexLpTokens = useMemo(
    () => [
      {
        symbol: "DAI/WETH",
        icon: 'media/tokens/dai-weth.svg',
        desc: "",
        maxLtv: (() => toPercentage(0.85))(),
        liquidationThreshold: (() => toPercentage(0.90))(),
      },
      {
        symbol: "USDC/WETH",
        icon: 'media/tokens/usdc-weth.svg',
        desc: "",
        maxLtv: (() => toPercentage(0.85))(),
        liquidationThreshold: (() => toPercentage(0.90))(),
      },
      {
        symbol: "USDT/WETH",
        icon: 'media/tokens/usdt-weth.svg',
        desc: "",
        maxLtv: (() => toPercentage(0.85))(),
        liquidationThreshold: (() => toPercentage(0.90))(),
      },
    ], []
  )

  const loanTokens = useMemo(
    () => [
      {
        symbol: "DAI",
        icon: 'media/tokens/dai-weth.svg',
        desc: "",
      },
      {
        symbol: "USDC",
        icon: 'media/tokens/usdc-weth.svg',
        desc: "",
      },
      {
        symbol: "USDT",
        icon: 'media/tokens/usdt-weth.svg',
        desc: "",
      },
      {
        symbol: "WETH",
        icon: 'media/tokens/usdt-weth.svg',
        desc: "",
      },
    ], []
  )

  const value = {
    dexLpTokens,
  }

  return (
    <Context.Provider value={value}>
      {children}
    </Context.Provider>
  )
}

export default KnowTokensProvider
