import { useEffect, useState } from "react";
import { useChain } from "../../../../chain/ChainProvider";
import Listbox from "../../../../components/listbox/Listbox";

const DashboardTableOptions = ({options}) => {

  const { activeChain, chains, changeChain } = useChain()

  const [selected, setSelected ] = useState(activeChain);

  const styles = {
    listbox: "w-60",
    button: {
      normal : "cursor-pointer rounded-2xl bg-table-options pl-3 pr-10 text-table-options-font text-center font-semibold  text-base font-Poppins",
      open: "",
      unopen : ""
    },
    options : "absolute mt-14 max-h-60 w-full overflow-auto rounded-xl shadow-bar-dropdown divide-y divide-soild bg-white text-base",
    option : {
      normal: '',
      active: '',
      deactive: ''
    }
  }

  useEffect(() => {
    if(selected.meta.chainId === activeChain.meta.chainId) {
      return
    }
    changeChain(selected.meta.chainId);
  }, [selected, activeChain, changeChain]);

  return (
    <Listbox 
      items={chains} 
      selected={ selected } 
      setSelected={setSelected}
      styles={styles}
    />
  )
}

export default DashboardTableOptions
