import { useState } from 'react'
import { Tab as TwTab } from '@headlessui/react'
import Card from '../cards/Card'

function classNames(...classes) {
  return classes.filter(Boolean).join(' ')
}

const Tab = ({ datas }) => {
  let [categories] = useState({
    "Dex LP Token": [
      {
        id: 1,
        title: 'Does drinking coffee make you smarter?',
        date: '5h ago',
        commentCount: 5,
        shareCount: 2,
      },
      {
        id: 2,
        title: "So you've bought coffee... now what?",
        date: '2h ago',
        commentCount: 3,
        shareCount: 2,
      },
    ],
    "Staking Token": [
      {
        id: 1,
        title: 'Is tech making coffee better or worse?',
        date: 'Jan 7',
        commentCount: 29,
        shareCount: 16,
      },
      {
        id: 2,
        title: 'The most innovative things happening in coffee',
        date: 'Mar 19',
        commentCount: 24,
        shareCount: 12,
      },
      {
        id: 3,
        title: 'The most innovative things happening in coffee',
        date: 'Mar 19',
        commentCount: 24,
        shareCount: 12,
      },
    ],
  })

  return (
    <TwTab.Group>
      <TwTab.List className="inline-flex space-x-4 rounded-2xl bg-tab-background py-2 px-4">
        {Object.keys(datas).map((data) => (
          <TwTab
            key={data}
            className={({ selected }) =>
              classNames(
                'rounded-lg py-2.5 px-4 text-base font-bold leading-5  text-nav-menu',
                'focus:outline-none',
                selected
                  ? 'bg-white tab-pledge-selected'
                  : 'hover:bg-white/[0.12]'
              )
            }
          >
            {data}
          </TwTab>
        ))}
      </TwTab.List>
      <TwTab.Panels className="mt-2">
        {Object.values(datas).map((items, idx) => (
          <TwTab.Panel
            key={idx}
          >
            <ul>
              <div className="mx-auto max-w-7xl grid grid-cols-3 gap-7 pt-11">
                {items.map((item, idx) => (
                  <Card 
                    key={idx}

                  />
                ))}
              </div>
            </ul>
          </TwTab.Panel>
        ))}
      </TwTab.Panels>
    </TwTab.Group>
  )
}

export default Tab
