import React, { useState } from "react";
import { createPortal } from "react-dom";

const Drawer = ({ children, drawerContent }) => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleDrawer = () => {
    setIsOpen(!isOpen);
  };

  const handleClose = () => {
    setIsOpen(false);
  };

  const drawer = (
    <div className="fixed inset-0 z-50 bg-black bg-opacity-50" onClick={handleClose}>
      <div className="w-80 bg-white h-screen overflow-auto p-4">
        <div className="flex justify-end">
          <button onClick={toggleDrawer}>
            <FontAwesomeIcon icon={faBars} />
          </button>
        </div>
        {drawerContent}
      </div>
    </div>
  );

  return (
    <>
      <button onClick={toggleDrawer}>
        click to drawer
      </button>
      {isOpen && createPortal(drawer, document.body)}
    </>
  );
};

export default Drawer;
