import { useState } from 'react'
import { Tab as TwTab } from '@headlessui/react'
import Card from '../cards/Card'

function classNames(...classes) {
  return classes.filter(Boolean).join(' ')
}

const Tab = () => {
  let [categories] = useState({
    "Dex LP Token": [
      {
        id: 1,
        title: 'Does drinking coffee make you smarter?',
        date: '5h ago',
        commentCount: 5,
        shareCount: 2,
      },
      {
        id: 2,
        title: "So you've bought coffee... now what?",
        date: '2h ago',
        commentCount: 3,
        shareCount: 2,
      },
    ],
    "Staking Token": [
      {
        id: 1,
        title: 'Is tech making coffee better or worse?',
        date: 'Jan 7',
        commentCount: 29,
        shareCount: 16,
      },
      {
        id: 2,
        title: 'The most innovative things happening in coffee',
        date: 'Mar 19',
        commentCount: 24,
        shareCount: 12,
      },
    ],
  })

  return (
    <TwTab.Group>
      <TwTab.List className="inline-flex space-x-4 rounded-2xl bg-tab-background py-2 px-4">
        {Object.keys(categories).map((category) => (
          <TwTab
            key={category}
            className={({ selected }) =>
              classNames(
                'rounded-lg py-2.5 px-4 text-base font-bold leading-5  text-nav-menu',
                'focus:outline-none',
                selected
                  ? 'bg-white tab-pledge-selected'
                  : 'hover:bg-white/[0.12]'
              )
            }
          >
            {category}
          </TwTab>
        ))}
      </TwTab.List>
      <TwTab.Panels className="mt-2">
        {Object.values(categories).map((posts, idx) => (
          <TwTab.Panel
            key={idx}
            className={classNames(
              'rounded-xl bg-white p-3',
              'ring-white ring-opacity-60 ring-offset-2 ring-offset-blue-400 focus:outline-none focus:ring-2'
            )}
          >
            <ul>
              <div>
                {posts.map((post) => (
                  <Card key={post}/>
                ))}
              </div>

            </ul>
          </TwTab.Panel>
        ))}
      </TwTab.Panels>
    </TwTab.Group>
  )
}

export default Tab
