import { Disclosure as TwDisclosure } from '@headlessui/react'
import { ChevronUpIcon } from '@heroicons/react/20/solid'

const Disclosure = () => {
  return (
    <div className="w-full px-4">
      <div className="mx-auto w-full max-w-md rounded-2x p-2 disclosure-dashbord">
        <TwDisclosure>
          {({ open }) => (
            <>
              <TwDisclosure.Button className="flex w-full justify-between rounded-lg bg-purple-100 px-4 py-2 text-left text-sm font-medium text-purple-900 hover:bg-purple-200 focus:outline-none focus-visible:ring focus-visible:ring-purple-500 focus-visible:ring-opacity-75">
                <span>What is your refund policy?</span>
                <ChevronUpIcon
                  className={`${
                    open ? 'rotate-180 transform' : ''
                  } h-5 w-5 text-purple-500`}
                />
              </TwDisclosure.Button>
              <TwDisclosure.Panel className="px-4 pt-4 pb-2 text-sm text-gray-500">
                If you're unhappy with your purchase for any reason, email us
                within 90 days and we'll refund you in full, no questions asked.
              </TwDisclosure.Panel>
            </>
          )}
        </TwDisclosure>
      </div>
    </div>
  )
}

export default Disclosure
